import React,{useCallback, useEffect, useState} from 'react'
import {Form,Button} from 'react-bootstrap'
import PropTypes from 'prop-types'
import { useDispatch, useSelector } from 'react-redux'
import useInput from '../hooks/useInput'
import {addComment, ADD_COMMENT_REQUEST, ADD_COMMENT_SUCCESS} from '../reducers/post'
const CommentForm = ({post}) => {
    const id = useSelector(state => state.user.me?.id);
    const dispatch = useDispatch();
    const [commentText, OnChangeCommentText,setCommentText] = useInput('')

    useEffect(() => {
        
        setCommentText
    }, [])
    const onSubmitComment = useCallback(
        (e) => {
            e.preventDefault();
            dispatch(addComment({
                content: commentText,
                postId: post.id,
                userId: id,
            }))
        },
        [commentText],
    )
    return (
        <>
            <Form onSubmit={onSubmitComment}>
                <Form.Group style={{position:'relative'}} controlId="exampleForm.ControlTextarea1">
                    <Form.Label srOnly>Example textarea</Form.Label>
                    <Form.Control as="textarea" rows={3} onChange={OnChangeCommentText}/>
                </Form.Group>
                <Button style={{position:'absolute', right:0,}} variant="primary" type="submit">
                게시
                </Button>
            </Form>
            
        </>
    )
}
CommentForm.propTypes = {
    post: PropTypes.object.isRequired,
}
export default CommentForm
