import React, { useCallback, useState } from 'react'

export default (initialValue= null) => {
    const [value, setValue] = useState(initialValue);
    const handler = useCallback(
        (e) => {
            setValue(e.target.value);
        },[]);
        return [value,handler,setValue]
        console.log(value)
}
