import React,{useCallback, useState} from 'react'
import {Form,Col,Button,Row} from 'react-bootstrap'
import Link from 'next/link'
import styled from 'styled-components'

const FormLabel = styled(Form.Label)`
    padding: 10px;
`

const LoginForm = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const onChangeEmail = useCallback(
        (e) => { 
            setEmail(e.target.value)
        },[]
    )
    const onChangePassword = useCallback(
        (e) => { 
            setPassword(e.target.value)
        },[]
    )
    return (
        <div>
            <Form>
                <Form.Group as={Row} controlId="formHorizontalEmail">
                    <FormLabel column sm={2}>
                    Email
                    </FormLabel>
                    <Col sm={10}>
                    <Form.Control type="email" placeholder="Email" value={email} onChange={onChangeEmail}/>
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formHorizontalPassword">
                    <FormLabel column sm={2}>
                    Password
                    </FormLabel>
                    <Col sm={10}>
                    <Form.Control type="password" placeholder="Password" value={password} onChange={onChangePassword} />
                    </Col>
                </Form.Group>
                
                <Form.Group as={Row}>
                    <Col sm={{ span: 10, offset: 2 }}>
                    <Button type="submit">로그인</Button>
                    <Link href="/signup"><Button variant="outline-primary">회원가입</Button></Link>
                    </Col>
                </Form.Group>
            </Form>
        </div>
    )
}

export default LoginForm
