import React, { useCallback, useRef } from 'react'
import {Form,Button} from 'react-bootstrap';
import styled from 'styled-components';


const PostForm = () => {
    const imageUpload = useRef()
    const HandleImageUpload = useCallback(
        (e) => {
            e.preventDefault();
            imageUpload.current.click();
        },
        [imageUpload.current],
    )
    return (
        <Form>
            <Form.Group>
                <Form.Label>게시물 작성하기</Form.Label>
                <Form.Control as="textarea" rows={3} />
                    <Button style={{float: 'right', marginTop: '1rem'}} type="submit">게시</Button>
                    <Form.File.Input ref={imageUpload}  hidden />
                    <Button style={{float: 'left', marginTop: '1rem'}} type="submit" onClick = {HandleImageUpload}>이미지업로드</Button>
            </Form.Group>
            
        </Form>
    )
}

export default PostForm
