import React, { useCallback, useRef } from 'react'
import {Form,Button} from 'react-bootstrap';
import styled from 'styled-components';
import {usedispatch} from 'react-redux'
import { addPostRequestAction } from '../reducers/post';

const PostForm = () => {
    const dispatch = useDispatch()
    const imageUpload = useRef()
    const ImageUploadHandler = useCallback(
        (e) => {
            e.preventDefault();
            imageUpload.current.click();
        },
        [imageUpload.current],
    )
    const UploadHandler = useCallback(
        (e) => {
            e.preventDefault();
            dispatch(addPostRequestAction({}))
        },
        [],
    )
    return (
        <Form>
            <Form.Group>
                <Form.Label>게시물 작성하기</Form.Label>
                <Form.Control as="textarea" rows={3} value={content} />
                    <Button style={{float: 'right', marginTop: '1rem'}} type="submit" onClick={UploadHandler}>게시</Button>
                    <Form.File.Input ref={imageUpload} multiple hidden />
                    <Button style={{float: 'left', marginTop: '1rem'}} type="submit" onClick = {ImageUploadHandler}>이미지업로드</Button>
            </Form.Group>
            
        </Form>
    )
}

export default PostForm
