import {all, fork, takeLatest, delay } from 'redux-saga/effects'
import axios from 'axios'
import { LOG_IN_FAILURE, LOG_IN_REQUEST, LOG_IN_SUCCESS } from '../reducers/user';

function loginApi(data){
    return axios.get('/api/login',data);
}
function* login() {
    try{
        yield delay(1000);
        yield put({
            type: LOG_IN_SUCCESS,
            data: action.data
        })

    }catch(err){
        yield put ({
            type: LOG_IN_FAILURE,
            error: err.response.data,
        })
    }
}

function* wathLogin() {
    yield takerLatest(LOG_IN_REQUEST, login)
}

export default function* userSaga(){
    yield all([
        fork(wathLogin),
    ])
}
