import React from 'react'
import {Button, Card} from 'react-bootstrap'
import {useSelector} from 'react-redux'
import styled from 'styled-components'

const CardLinkWrapper = styled.div`
    padding-top : 15px;
    border-top: 1px solid rgba(0, 0, 0, 0.125);
    display: flex;

`
const LogoutButton = styled(Button)`
    margin-bottom: 15px;
`
const CardLink = styled(Card.Link)`
    padding : 5px;
    text-align: center;
`
const UserProfile = () => {
    const dummyUser = (data) => ({
    ...data,
    nickname:'희도',
    id:1,
    Posts:[{id: 1}],
    Followings:[{nickname:'부기'}],
    Followers:[{nickname: 'king'}]

})
    const {me} = useSelector(state => state.user)
    return (
        <Card style={{ width: '33rem', flexDirection: 'row' }}>
            <Card.Img variant="left" src="https://images.unsplash.com/photo-1522075469751-3a6694fb2f61?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=80" width='155' />
            <Card.Body>
                <Card.Title>희도</Card.Title>
                <LogoutButton>로그아웃</LogoutButton>
                <br/>
                <CardLinkWrapper>
                    <CardLink href="#">게시글 수 <br/>1</CardLink>
                    <CardLink href="#">팔로잉 <br/>1</CardLink>
                    <CardLink href="#">팔로워 <br/>1</CardLink>
                </CardLinkWrapper>
                
            </Card.Body>
        </Card>
    )
}

export default UserProfile
