import React, { useCallback, useState } from 'react'
import {Card,Button} from 'react-bootstrap'
import { useSelector } from 'react-redux'
import ImagesZoom from './ImagesZoom'
const PostCard = ({post}) => {
    const mainpost = useSelector(state => state.post.mainPosts)
    const [showImagesZoom, setImageZoom] = useState(false)
    const onZoom = useCallback(
        () => {
            setImageZoom(true)
        },
        [],
    )
    const onClose = useCallback(
        () => {
            setImageZoom(false)
        },
        [],
    )
    const showImages = mainpost.map((v) => {
        if(v.Images.length === 1){
         return  (
             <>
         <Card.Img style={{width: "100%", }} variant="top" alt={v.Images[0].src} src={v.Images[0].src} onClick={onZoom} />
            {showImagesZoom && <ImagesZoom images={v.Images} onClose={onClose} />}

             </>
         )
        }
        if(v.Images.length === 2){
         return  (
             <>
         <Card.Img style={{width: "50%" }}  variant="top" alt={v.Images[0].src} src={v.Images[0].src} onClick={onZoom} />
         <Card.Img style={{width: "50%" }} variant="top" alt={v.Images[1].src} src={v.Images[1].src} onClick={onZoom} />
            {showImagesZoom && <ImagesZoom images={v.Images} onClose={onClose} />}

             </>
         )
        }
        else{
            return  (
             <>
         <Card.Img style={{width: "50%" }}  variant="top" alt={v.Images[0].src} src={v.Images[0].src} onClick={onZoom} />
         <Card.Img style={{width: "50%" }} variant="top" alt={v.Images[1].src} src={v.Images[1].src} onClick={onZoom} />
            {showImagesZoom && <ImagesZoom images={v.Images} onClose={onClose} />}

             </>
         )
        }
    })
    return (
        <div style={{margin: '100px 0 20px'}}>
            <Card style={{ width: '33rem'}}>
                {showImages}
                <Card.Body>
                    <Card.Title>Card Title</Card.Title>
                    <Card.Text>
                    Some quick example text to build on the card title and make up the bulk of
                    the card's content.
                    </Card.Text>
                    <Button variant="primary">Go somewhere</Button>
                </Card.Body>
            </Card>
        </div>
    )
}

export default PostCard
