import { all, fork, take, put, takeEvery, takeLatest, delay } from 'redux-saga/effects';

import userSaga from './user';

// take는 LOG_IN이라는 액션이 실행되면 logIn을 실행
export default function* rootSaga() {
  yield all([
    fork(userSaga),
  ]);
}

//fork는 함수를 실행한다.
//call도 동일 차이점은
